import { Component } from '@angular/core';
import { Player } from './models/player.model';
import { OpportunityEngineService } from './services/opportunity-engine.service';
import { ContributionCalculatorService } from './services/contribution-calculator.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  providers:[OpportunityEngineService,ContributionCalculatorService]
})
export class AppComponent {

  step = 1;
  innings1 = '';
  innings2 = '';
  teams:string[] = [];
  selectedTeam = '';
  amount = 2000;
  players:Player[] = [];

  constructor(private opp:OpportunityEngineService,
              private calc:ContributionCalculatorService){}

  detectTeams(){
    const text = this.innings1 + this.innings2;
    const matches = text.match(/\(([^)]+)\)/g) || [];
    this.teams = [...new Set(matches.map(m=>m.replace(/[()]/g,'')))];
    this.step = 2;
  }

  calculate(){
    const team1 = this.extractTeam(this.innings1);
    const team2 = this.extractTeam(this.innings2);
    const players1 = this.parse(this.innings1, team1);
    const players2 = this.parse(this.innings2, team2);
    const merged = [...players1, ...players2];
    const map: any = {};
    merged.forEach(p=>{
      if(!map[p.name]) map[p.name]={...p};
      else{
        map[p.name].ballsFaced+=p.ballsFaced;
        map[p.name].ballsBowled+=p.ballsBowled;
        map[p.name].runs+=p.runs;
        map[p.name].wickets+=p.wickets;
      }
    });
    this.players = Object.values(map);
    this.players = this.players.filter(p => p.team === this.selectedTeam);
    this.players = this.opp.calculate(this.players);
    this.players = this.calc.calculate(this.players,this.amount);
    this.step = 3;
  }

  private extractTeam(text: string): string {
    const match = text.match(/\(([^)]+)\)/);
    return match ? match[1] : '';
  }

  parse(text:string, team: string):Player[]{
    const lines = text.split('\n').map(l=>l.trim()).filter(Boolean);
    const players:Player[]=[];
    let bat=false,bowl=false;

    for(const l of lines){
      if(l.startsWith('No Batsman')){bat=true;bowl=false;continue;}
      if(l.startsWith('No Bowler')){bowl=true;bat=false;continue;}
      if(l.startsWith('Extras')) bat=false;

      if(bat && /^\d+\s/.test(l)){
        const p=l.split(/\s+/);
        players.push({
          name:p[1].replace(/\(.+?\)/g,''),
          battingPosition:+p[0],
          ballsFaced:+p[p.length-5],
          runs:+p[p.length-6],
          strikeRate:+p[p.length-1],
          ballsBowled:0,
          wickets:0,
          economy:0
        });
      }

      if(bowl && /^\d+\s/.test(l)){
        const p=l.split(/\s+/);
        const name=p[1];
        const overs=p[p.length-8];
        const wickets=+p[p.length-5];
        const eco=+p[p.length-1];
        const balls=this.toBalls(overs);
        const pl=players.find(x=>name.includes(x.name));
        if(pl){
          pl.ballsBowled=balls;
          pl.wickets=wickets;
          pl.economy=eco;
        }
      }
    }
    return players;
  }

  toBalls(o:string):number{
    if(!o.includes('.')) return +o*6;
    const [x,y]=o.split('.');
    return +x*6+ +y;
  }
}
